      <div v-else class="game-rps-hands-bottom">
        <div @click="selectedHand = index" :class="{selected: selectedHand === index}" :key="item.link" v-for="(item,index) in hands">
          <img :src="require(`@/assets/rps-${item.link}.svg`)" :alt="item.name">
          <p>{{ item.name }}</p>
        </div>
      </div>


    </div>
    <div class="game-rps-filters">
      <div class="game-rps-filters-filter x2">
        <p>Your bet</p>
        <div class="game-rps-filters-filter-wrap">
          <div class="game-rps-filters-filter-wrap-bet">
            <div class="input">
              <span class="minus" @click="changeBetAmount(1,true)">-</span>
              <input type="text" v-model.lazy="betAmount">
              <span class="plus" @click="changeBetAmount(1)">+</span>
            </div>
            <button class="btn btn-red clear" @click="clearBetAmount()">Clear</button>
            <button class="btn btn-red" @click="changeBetAmount(50000000)">+0.5</button>
            <button class="btn btn-red" @click="changeBetAmount(100000000)">+1</button>
            <button class="btn btn-red" @click="changeBetAmount(1000000000)">+10</button>
            <button class="btn btn-red" @click="changeBetAmount(10000000000)">+100</button>
            <button class="btn btn-red" @click="halfBetAmount">1/2</button>
            <button class="btn btn-red" @click="x2BetAmount">x2</button>
          </div>
        </div>
      </div>
      <div class="game-rps-filters-play x1">
        <div class="select">
          <select v-model="gamesCount">
            <option v-for="(item, key) in playTimes" :key="key" :value="item">
              {{ item }}x
            </option>
          </select>
          <button class="btn btn-green" @click="createGame(selectedHand ,selectCurrency, parseFloat(betAmount).toFixed(8), gamesCount)">Play!</button>
        </div>
      </div>
    </div>
    <div class="game-rps-matches">
      <rps-match v-for="item in games" :key="item.id" :item="item" @joinGame="joinGame" @endMatch="endMatch" />
