<script>
import VueSlickCarousel from 'vue-slick-carousel'
import 'vue-slick-carousel/dist/vue-slick-carousel.css'
import RpsMatch from "./RpsMatch";
export default {
  name: "Rps",
  components: {
    VueSlickCarousel,
    RpsMatch
  },
  data() {
    return {
      bet: 0,
      playTimes: [1, 2, 3],
      gamesCount: 1,
      selectedHand: 0,
      hands: [
        {link:'dbscissors', name: 'Double Scissors'},
        {link:'stone', name: 'Stone'},
        {link:'scissors', name: 'Scissors'},
        {link:'ring', name: 'Ring'},
        {link:'paper', name: 'Paper'},
      ],
      showModal: false,
      settings: {
        arrows: true,
        dots: false,
        slidesToShow: 4,
        infinite: true,
        responsive: [
          {
            "breakpoint": 600,
            "settings": {
              "centerMode": true,
              "centerPadding": "40px",
              "slidesToShow": 2,
              "slidesToScroll": 1,
              "initialSlide": 1
            }
          },
          {
            "breakpoint": 480,
            "settings": {
              "centerMode": true,
              "centerPadding": "40px",
              "slidesToShow": 1,
              "slidesToScroll": 1
            }
          }
        ]
      },
    }
  },
  methods: {
    enterLobby: function() {
      this.$store.dispatch('sendSocketGameRps', true)
    },
    exitLobby: function() {
      this.$store.dispatch('sendSocketGameRps', false)
    },
    createGame: function(hand, currency, amount, count) {
      this.$store.dispatch('sendSocketGameRpsCreate', {figure: hand, currency: currency, amount: amount, count: count})
    },
    createGameAnswer: function(data) {
      this.$store.commit('answer_game_coin_flip_create', null)

      if (data.status === "error") {
        this.$store.dispatch('addNotif', { type: 'danger', text: data.message, isClose: false, timer: 2000 }).then()
      }
    },
    joinGame: function(id) {
      this.$store.dispatch('sendSocketGameRpsJoin', {id: id, figure: this.selectedHand})
    },
    changeBalance: function (e, type) {
      if (type === 'from') this.changeFromBalance(e)
      if (type === 'to') this.changeToBalance(e)
    },
    endMatch(index) {
      this.$delete(this.games, this.games.indexOf(index));
    },
    changeBetAmount(val, min = false) {
      if (!min) {
        if (this.$store.getters.getBalance >=  (this.bet + val) / 100000000)  {
          this.bet += val;
        }
      } else {
        this.bet -= val;
      }
    },
    clearBetAmount() {
      this.bet = 1
    },
    setMaxBetAmount() {
      this.bet = this.$store.getters.getBalance * 100000000;
    },
    x2BetAmount()  {
      this.bet *= 2;
    },
    halfBetAmount() {
      this.bet /=  2;
    }
  },
  computed: {
    betAmount: {
      get(){
        return (this.bet / 100000000).toFixed(8)
      },
      set(val) {
        val = val.replace(/,/g, '.');
        let res = (+(+val).toFixed(8)) *  100000000;
        let regex = /[0-9]|\./;
        if( regex.test(res) ) {
          this.bet = (+(+val).toFixed(8)) *  100000000;
        } else {
          this.bet = 0
        }
      }
    },
    balances() {
      return this.$store.getters.getBalances;
    },
    games() {
      return this.$store.getters.getGameLobbyRpsGames;
    },
    subscribeCreate() {
      return this.$store.getters.getSubscribesRpsCreate;
    },
    selectCurrency() {
      return this.$store.getters.getSelectCurr;
    }
  },
  watch: {
    bet: function() {
      this.bet < 0? this.bet = 0: '';
    },
    subscribeCreate: function(value) {
      if (value === null) return
      this.createGameAnswer(value)
    }
  },
  created () {
    this.enterLobby()
  },
  beforeDestroy () {
    this.exitLobby()
  },
}
</script>

